const J2NET_HEADER =                    77;
const J2NET_HEADER_SIZE	=		        3;

const J2NET_PART_WELCOME =			    10;
const J2NET_PART_ALIVE =			    11;
const J2NET_PART_CAR_KNOCK =			30;
const J2NET_PART_CAR_QUESTION =		    31;
const J2NET_PART_CAR_ANSWER	=		    32;
const J2NET_PART_TEMP =				    50;
const J2NET_PART_PULSE_COUNT =		    51;
const J2NET_PART_TEMP_HUMI =			52;
const J2NET_PART_NTP =				    53;
const J2NET_PART_RELAY_SET =			54;
const J2NET_PART_RELAY_GET =			55;
const J2NET_PART_WEATHER =			    60;
const J2NET_PART_WEATHERX =			    61;
const J2NET_PART_HOME_STATUS_FAST =		65;
const J2NET_PART_HOME_STATUS_SLOW =		66;
const J2NET_PART_DOOR_BELL =			68;
const J2NET_PART_VCC =				    70;


if (typeof(msg.payload)=="string" && msg.payload.startsWith("MSG")) {
    var data = msg.payload.substr(4,msg.payload.length-7).split(" ");

    for (var i=0; i<data.length; i++) {
        data[i]= parseInt(data[i]);
    }
    var odata = {};

    if (data.length>=3 && data[0]==J2NET_HEADER) {
        var nodeid = data[1];
        var seq = data[2];
        odata.seq = seq;
        var cursor = 3;

        while (cursor<data.length) {
            switch (data[cursor]) {
                case J2NET_PART_TEMP:
                    var t = ((data[cursor+1] | data[cursor+2] << 8))/10;
                    odata.t = t;
                    cursor+=3;
                break;

                case J2NET_PART_TEMP_HUMI:
                    //t = ((float)(elements[cursor+1].toInt() | elements[cursor+2].toInt() << 8))/10;
                    //h = ((float)(elements[cursor+3].toInt() | elements[cursor+4].toInt() << 8))/10;
                    cursor+=5;
                break;
                case J2NET_PART_PULSE_COUNT:
                    var w = data[cursor+1] | data[cursor+2] << 8;
                    odata.w=w;
                    cursor+=3;
                break;
                case J2NET_PART_DOOR_BELL:
                    cursor+=2;
                break;

                case J2NET_PART_VCC:
                    var bat=((1+data[cursor+1]*0.02)*100)/100;
                    odata.bat=bat;
                    cursor+=2;
                break;
                default:
                    node.error("unknown/broken UMSG part");
                    cursor++;
            }

        }
    }

    msg.payload = JSON.stringify(odata);
    msg.topic = "j5net/nodes/gw-1/"+nodeid;
}
return msg;

